import './App.css';
import React, {Component, useState} from "react";

function App() {
        const [show, setShow] = useState(false);
        const [isYellow, setIsYellow] = useState(false)

        return (
            <>
                <button className="bu" onClick={() => setShow(!show)} onChange={() => setIsYellow(isYellow)} hidden={show}>
                </button>
                {
                    show && isYellow?
                        <div>
                            <button className="bu-red" hidden={!show}></button>
                        </div>
                        :<div>
                            <button className="bu-yellow" hidden={!show}></button>
                        </div>
                }
            </>
        );
}

export default App;
