import './App.css';
import React, {Component, useState} from "react";

function App() {
        const [show, setShow] = useState(false);
        const [show1, setShow1] = useState(false);

        const [isYellow, setIsYellow] = useState(true);

        return (
            <>
                <button className="bu" onClick={() => setShow(!show)} onChange={() => setIsYellow(!isYellow)} hidden={show}>
                </button>
                {
                    show && isYellow
                        ?
                        <div>
                            <button className="bu-yellow" hidden={!show}/>
                        </div>
                        :
                        <div>
                            <button className="bu-red" hidden={!show}/>
                        </div>
                }

                <button className="bu" onClick={() => setShow1(!show1)} onChange={() => setIsYellow(!isYellow)} hidden={show1}>
                </button>
                {
                    show1 && isYellow
                        ?
                        <div>
                            <button className="bu-yellow" hidden={!show1}/>
                        </div>
                        :
                        <div>
                            <button className="bu-red" hidden={!show1}/>
                        </div>
                }
            </>
        );
}

export default App;
