import './App.css';
import React, {Component, useState} from "react";

class App extends Component {
    // states of rows and columns
    state = {
        rows: 6,
        columns: 7
    };

    // render a square board
    renderBoard() {
        /* get number of rows and columns from state */
        const { rows, columns } = this.state;
        const rowViews = [];

        /* create rows and columns for the board */
        for (let row = 0; row < this.state.rows; row += 1) {
            const columnViews = [];
            for (let column = 0; column < this.state.columns; column += 1) {
                columnViews.push(<div className="column"></div>);
            }
            rowViews.push(
                <div className="row">{columnViews}</div>
            );
        }

        return (
            <div className="board">
                {rowViews}
            </div>
        )
    }

    render() {

        return (
            <>
                <div className="container">
                    <div>
                        {this.renderBoard()}
                    </div>
                </div>
            </>
        )
    }

}

export default App;
