import './App.css';
import React, { useState } from "react";

function App() {
    const initialState = [
        {
            person: "Ela",
            age: 48
        },
        {
            person: "Natalia",
            age: 28
        }
    ];

    const green = "#39D1B4";
    const yellow = "#FFD712";

    function Person({ person, onClick, color, index }) {
        return (
            <>
                <button
                    style={{ backgroundColor: color }}
                    color={color}
                    onClick={onClick}
                    index={index}
                >
                    Change color
                </button>
                {index}
            </>
        );
    }

    const [buttonColor, setButtonColor] = useState({0:"green",1:"green"});

    function handleColorChange(e,i) {
        console.log(i)
        const button = e.target.style.backgroundColor;
        const newButton = e.target.style.backgroundColor;

        const newColor = buttonColor[i] === green ? yellow : green;
        const newState ={...buttonColor,[i]:newColor}
        setButtonColor(newState);
    }

    return (
        <>
            <div className="App">
                <header className="header">CONNECT 4 GAME</header>
                <div className="App">
                    {person.map((per, i) => {
                        return (
                            <Person
                                color={buttonColor[i]}
                                key={i}
                                index={i}
                                onClick={(e) => handleColorChange(e,i)}
                            />
                        );
                    })}
                </div>
            </div>
        </>
    )
}

export default App;
