import './App.css';
import React, {Component, useState} from "react";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isYellow: true, className: "bu", buttonIndex: -1
        }
    }

    handleClick = (indexValue) => {
        if (this.state.isYellow) {
            this.setState({
