import './App.css';
import React, {Component, useState} from "react";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            buttonIndex: -1
        }
    }

    handleClick = (indexValue, isClickedValue) => {
        this.setState({
            buttonIndex: indexValue
        })
    }

    handleChange = (indexV) => {
        this.setState({
            buttonIndex: -1
        })
    }

    render() {
        return (
            <>
                <div className="App">
                    <header className="header">
                        CONNECT 4 GAME
                    </header>
                    <div className="button-grid">
                        <button className={this.state.buttonIndex === 0 ? "bu-red" : "bu"} onClick={() => this.handleClick(0 )} onChange={() => this.handleChange(1)}/>
                        <button className={this.state.buttonIndex === 1 ? "bu-red" : "bu"} onClick={() => this.handleClick(1 )} onChange={() => this.handleChange(-1)}/>
                        <button className={this.state.buttonIndex === 2 ? "bu-red" : "bu"} onClick={() => this.handleClick(2 )} onChange={() => this.handleChange(-1)}/>
                        <button className={this.state.buttonIndex === 3 ? "bu-red" : "bu"} onClick={() => this.handleClick(3 )} onChange={() => this.handleChange(-1)}/>
                    </div>
                </div>
            </>
        )
    }

}

export default App;
