import './App.css';
import React, {Component, useState} from "react";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            buttonIndex: -1
        }
    }

    handleClick = (value) => {
        this.setState({
            buttonIndex: value
        })
    }

    render() {
        return (
            <>
                <div className="App">
                    <header className="header">
                        CONNECT 4 GAME
                    </header>
                    <div className="button-grid">
                        <button className="bu" onClick={() => this.handleClick(0)}/>
                        <button className="bu" onClick={() => this.handleClick(1)}/>
                        <button className="bu" onClick={() => this.handleClick(2)}/>
                        <button className="bu" onClick={() => this.handleClick(3)}/>
                    </div>

                    <div>
                        {
                            this.state.buttonIndex === 0 && <div>its general</div>
                        }
                        {
                            this.state.buttonIndex === 1 && <div>its army</div>
                        }
                        {
                            this.state.buttonIndex === 2 && <div>its police</div>
                        }
                        {
                            this.state.buttonIndex === 3 && <div>its spy</div>
                        }
                    </div>
                </div>
            </>
        )
    }

}

export default App;
