import './App.css';
import React, {Component, useState} from "react";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            buttonIndex: -1, isHidden: false
        }
    }

    handleClick = (indexValue) => {
        this.setState({
            buttonIndex: indexValue
        })
    }

    render() {
        return (
            <>
                <div className="App">
                    <header className="header">
                        CONNECT 4 GAME
                    </header>
                    <div className="button-grid">
                        <button className="bu" onClick={() => this.handleClick(0, true)} hidden={this.state.isHidden}/>
                        <button className={this.state.buttonIndex === 0 ? "bu-red" : "bu-yellow"} />

                        <button className={this.state.buttonIndex === 1 ? "bu-red" : "bu"} onClick={() => this.handleClick(1 )}/>
                        <button className={this.state.buttonIndex === 2 ? "bu-red" : "bu"} onClick={() => this.handleClick(2 )}/>
                        <button className={this.state.buttonIndex === 3 ? "bu-red" : "bu"} onClick={() => this.handleClick(3 )}/>
                    </div>
                </div>
            </>
        )
    }

}

export default App;
