import './App.css';
import React, {Component, useState} from "react";

class App extends Component {
    state = { isClicked: false, isYellowTurn: false };

    handleClick = () => {
        this.setState({ isClicked: !this.state.isClicked, isYellowTurn: !this.state.isYellowTurn});
    };

    render() {
        const isClicked = this.state.isClicked;
        const isYellowTurn = this.state.isYellowTurn;

        return (
            <button className = { !isClicked && !isYellowTurn ? "bu-yellow" : "bu-red" } onClick = { this.handleClick }>
                {/*<h1>Hello react</h1>*/}
                {/*<button onClick={ this.handleToggle }>Toggle class</button>*/}
            </button>
        );
    }
}

export default App;
