import './App.css';
import React, {Component, useState} from "react";

class App extends Component {
    state = { isClicked: false, isYellowTurn: false, isHidden: false };

    handleClick = () => {
        this.setState({ isClicked: !this.state.isClicked, isYellowTurn: !this.state.isYellowTurn, isHidden: true});
    };

    render() {
        const isClicked = this.state.isClicked;
        const isYellowTurn = this.state.isYellowTurn;

        return (
            <button className = "bu" onClick={this.handleClick} hidden={this.state.isHidden}>
                {/*<h1>Hello react</h1>*/}
                <button className={isClicked && !isYellowTurn ? "bu-yellow" : "bu-red"} onClick={this.handleClick}/>
            </button>
        );
    }
}

export default App;
