import './App.css';
import React, {Component, useState} from "react";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            buttonIndex: -1, isHidden: false, isYellow: false
        }
    }

    handleClick = (indexValue, isHiddenValue) => {
        this.setState({
            buttonIndex: indexValue, isHidden: isHiddenValue, isYellow: !this.state.isYellow
        })
    }

    render() {
        return (
            <>
                <div className="App">
                    <header className="header">
                        CONNECT 4 GAME
                    </header>
                    <div className="button-grid">
                        <button className="bu" onClick={() => this.handleClick(0, true)} hidden={this.state.isHidden}/>
                        <button className={this.state.button === 0 && !this.state.isYellow ? "bu" : "bu-yellow"} hidden={!this.state.isHidden}/>
                        {/*<button className={this.state.buttonIndex === 0 && !this.state.isYellow ? "bu-red" : "bu"} hidden={!this.state.isYellow}/>*/}

                        {/*<button className="bu" onClick={() => this.handleClick(1, true)} hidden={this.state.isHidden}/>*/}
                        {/*<button className={this.state.buttonIndex === 1 && this.state.isYellow ? "bu-red" : "bu-yellow"} hidden={!this.state.isHidden}/>*/}

                    </div>
                </div>
            </>
        )
    }

}

export default App;
